import{_ as e,c as o}from"./app.9fd9aa02.js";const c={},p=o('<h1 id="\u7B80\u5355\u4ECB\u7ECD" tabindex="-1"><a class="header-anchor" href="#\u7B80\u5355\u4ECB\u7ECD" aria-hidden="true">#</a> \u7B80\u5355\u4ECB\u7ECD</h1><h2 id="\u4E3A\u4EC0\u4E48\u8981\u6709\u5185\u5B58\u6A21\u578B" tabindex="-1"><a class="header-anchor" href="#\u4E3A\u4EC0\u4E48\u8981\u6709\u5185\u5B58\u6A21\u578B" aria-hidden="true">#</a> \u4E3A\u4EC0\u4E48\u8981\u6709\u5185\u5B58\u6A21\u578B\uFF1F</h2><blockquote><p>\u60F3\u8981\u56DE\u7B54\u8FD9\u4E2A\u95EE\u9898\uFF0C\u6211\u4EEC\u5F97\u5148\u4E86\u89E3\u6E05\u695A\u8BA1\u7B97\u673A\u786C\u4EF6\u5185\u5B58\u7ED3\u6784</p></blockquote><h3 id="\u8BA1\u7B97\u673A\u786C\u4EF6\u5185\u5B58\u67B6\u6784" tabindex="-1"><a class="header-anchor" href="#\u8BA1\u7B97\u673A\u786C\u4EF6\u5185\u5B58\u67B6\u6784" aria-hidden="true">#</a> \u8BA1\u7B97\u673A\u786C\u4EF6\u5185\u5B58\u67B6\u6784</h3><blockquote><p>4\u6838CPU\u8BFB\u53D6\u5185\u5B58\u6570\u636E\u6A21\u578B</p></blockquote><p><img src="https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/c3266317359e486ca80e8a6417d77290~tplv-k3u1fbpfcp-zoom-1.image" alt="image-20221002185757916"></p><blockquote><p>\u89E3\u91CA</p></blockquote><ul><li><p><strong>CPU \u5BC4\u5B58\u5668\uFF08CPU Register\uFF09</strong></p><blockquote><p>\u4EC0\u4E48\u662F CPU \u5BC4\u5B58\u5668\uFF1F</p></blockquote><p>CPU\u5BC4\u5B58\u5668\u662FCPU\u5185\u90E8\u975E\u5E38\u5C0F\u3001\u975E\u5E38\u5FEB\u901F\u7684\u5B58\u50A8\u90E8\u4EF6\uFF0C\u5B83\u7684\u5BB9\u91CF\u5F88\u6709\u9650\u3002\u73B0\u4EE3CPU\u90FD\u5185\u7F6E\u4E86\u51E0\u5341\u4E2A\u751A\u81F3\u4E0A\u767E\u4E2A\u5BC4\u5B58\u5668\u3002\u4E00\u822C\u6211\u4EEC\u8BF4PC\u662F\u591A\u5C11\u4F4D\u7684\u5B9E\u9645\u6307\u7684\u5C31\u662FCPU\u5BC4\u5B58\u5668\u7684\u4F4D\u6570\u3002</p><blockquote><p>\u4E3A\u4EC0\u4E48\u8981 CPU \u5BC4\u5B58\u5668\uFF1F</p></blockquote><p>\u867D\u7136\u5185\u5B58\u7684\u8BFB\u53D6\u901F\u5EA6\u5DF2\u7ECF\u5F88\u5FEB\u4E86\uFF0C\u4F46\u662F\u548CCPU\u6BD4\u8D77\u6765\uFF0C\u8FD8\u662F\u6709\u5F88\u5927\u5DEE\u8DDD\u7684\uFF0C\u4E0D\u662F\u4E00\u4E2A\u6570\u91CF\u7EA7\u7684\u3002\u5982\u679C\u6BCF\u6B21\u90FD\u4ECE\u5185\u5B58\u4E2D\u8BFB\u53D6\u6570\u636E\uFF0C\u4F1A\u4E25\u91CD\u62D6\u6162CPU\u7684\u8FD0\u884C\u901F\u5EA6\uFF0CCPU\u7ECF\u5E38\u5904\u4E8E\u7B49\u5F85\u72B6\u6001\uFF0C\u65E0\u4E8B\u53EF\u505A\u3002\u5728CPU\u5185\u90E8\u8BBE\u7F6E\u4E00\u4E2A\u7F13\u5B58\uFF0C\u53EF\u4EE5\u5C06\u4F7F\u7528\u9891\u7E41\u7684\u6570\u636E\u6682\u65F6\u8BFB\u53D6\u5230\u7F13\u5B58\uFF0C\u9700\u8981\u540C\u4E00\u5730\u5740\u4E0A\u7684\u6570\u636E\u65F6\uFF0C\u5C31\u4E0D\u7528\u5927\u8001\u8FDC\u5730\u518D\u53BB\u8BBF\u95EE\u5185\u5B58\uFF0C\u76F4\u63A5\u4ECE\u7F13\u5B58\u4E2D\u8BFB\u53D6\u5373\u53EF\u3002</p></li><li><p><strong>Cache \u5B58\u50A8\u5668\uFF08Cache Memory\uFF09</strong></p><blockquote><p>\u4EC0\u4E48\u662F Cache \u5B58\u50A8\u5668\uFF1F</p></blockquote><p>Cache\u5B58\u50A8\u5668\u662F\u4F4D\u4E8ECPU\u548C\u4E3B\u5B58\u4E4B\u95F4\uFF0C\u89C4\u6A21\u8F83\u5C0F\uFF0C\u4F46\u901F\u5EA6\u5F88\u9AD8\u7684\u5B58\u50A8\u5668\u3002\u901A\u5E38\u7531<code>SRAM</code>\uFF08\u9759\u6001\u5B58\u50A8\u5668\uFF09\u7EC4\u6210\u3002</p><blockquote><p>\u4E3A\u4EC0\u4E48\u8981 Cache \u5B58\u50A8\u5668\uFF1F</p></blockquote><p>CPU\u7684\u901F\u5EA6\u8FDC\u9AD8\u4E8E\u5185\u5B58\uFF0C\u5F53CPU\u76F4\u63A5\u4ECE\u5185\u5B58\u4E2D\u5B58\u53D6\u6570\u636E\u65F6\u8981\u7B49\u5F85\u4E00\u5B9A\u65F6\u95F4\u5468\u671F\uFF0C\u800CCache\u5219\u53EF\u4EE5\u4FDD\u5B58CPU\u521A\u7528\u8FC7\u6216\u5FAA\u73AF\u4F7F\u7528\u7684\u4E00\u90E8\u5206\u6570\u636E\uFF0C\u5982\u679CCPU\u9700\u8981\u518D\u6B21\u4F7F\u7528\u8BE5\u90E8\u5206\u6570\u636E\u65F6\u53EF\u4ECECache\u4E2D\u76F4\u63A5\u8C03\u7528\uFF0C\u8FD9\u6837\u5C31\u907F\u514D\u4E86\u91CD\u590D\u5B58\u53D6\u6570\u636E\uFF0C\u51CF\u5C11\u4E86CPU\u7684\u7B49\u5F85\u65F6\u95F4\uFF0C\u56E0\u800C\u63D0\u9AD8\u4E86\u7CFB\u7EDF\u7684\u6548\u7387\u3002</p></li></ul><p>\u7531\u4E8E\u4E3B\u5B58\u4E0E CPU \u7684\u8FD0\u7B97\u80FD\u529B\u4E4B\u95F4\u6709\u6570\u91CF\u7EA7\u7684\u5DEE\u8DDD\uFF0C\u6240\u4EE5\u5728\u4F20\u7EDF\u8BA1\u7B97\u673A\u5185\u5B58\u67B6\u6784\u4E2D\u4F1A\u5F15\u5165\u9AD8\u901F\u7F13\u5B58\u6765\u4F5C\u4E3A\u4E3B\u5B58\u548C\u5904\u7406\u5668\u4E4B\u95F4\u7684\u7F13\u51B2\uFF0CCPU \u5C06\u5E38\u7528\u7684\u6570\u636E\u653E\u5728\u9AD8\u901F\u7F13\u5B58\u4E2D\uFF0C\u8FD0\u7B97\u7ED3\u675F\u540E CPU \u518D\u8BB2\u8FD0\u7B97\u7ED3\u679C\u540C\u6B65\u5230\u4E3B\u5B58\u4E2D\u3002</p><blockquote><p>\u4F7F\u7528\u9AD8\u901F\u7F13\u5B58\u89E3\u51B3\u4E86 CPU \u548C\u4E3B\u5B58\u901F\u7387\u4E0D\u5339\u914D\u7684\u95EE\u9898\uFF0C\u4F46\u540C\u65F6\u53C8\u5F15\u5165\u53E6\u5916\u4E00\u4E2A\u65B0\u95EE\u9898\uFF1A<code>\u7F13\u5B58\u4E00\u81F4\u6027\u95EE\u9898</code>\u3002</p></blockquote><h3 id="\u7F13\u5B58\u4E00\u81F4\u6027\u95EE\u9898" tabindex="-1"><a class="header-anchor" href="#\u7F13\u5B58\u4E00\u81F4\u6027\u95EE\u9898" aria-hidden="true">#</a> \u7F13\u5B58\u4E00\u81F4\u6027\u95EE\u9898</h3><blockquote><p>\u4EC0\u4E48\u662F\u7F13\u5B58\u4E00\u81F4\u6027\u95EE\u9898\uFF1F</p></blockquote><p>\u5728\u591ACPU\u7684\u7CFB\u7EDF\u4E2D(\u6216\u8005\u5355CPU\u591A\u6838\u7684\u7CFB\u7EDF)\uFF0C\u6BCF\u4E2ACPU\u5185\u6838\u90FD\u6709\u81EA\u5DF1\u7684\u9AD8\u901F\u7F13\u5B58\uFF0C\u5B83\u4EEC\u5171\u4EAB\u540C\u4E00\u4E3B\u5185\u5B58(Main Memory)\u3002<strong>\u5F53\u591A\u4E2ACPU\u7684\u8FD0\u7B97\u4EFB\u52A1\u90FD\u6D89\u53CA\u540C\u4E00\u5757\u4E3B\u5185\u5B58\u533A\u57DF\u65F6\uFF0CCPU \u4F1A\u5C06\u6570\u636E\u8BFB\u53D6\u5230\u7F13\u5B58\u4E2D\u8FDB\u884C\u8FD0\u7B97\uFF0C\u8FD9\u53EF\u80FD\u4F1A\u5BFC\u81F4\u5404\u81EA\u7684\u7F13\u5B58\u6570\u636E\u4E0D\u4E00\u81F4\u3002</strong></p><p>\u56E0\u6B64\u9700\u8981\u6BCF\u4E2A CPU \u8BBF\u95EE\u7F13\u5B58\u65F6\u9075\u5FAA\u4E00\u5B9A\u7684\u534F\u8BAE\uFF0C\u5728\u8BFB\u5199\u6570\u636E\u65F6\u6839\u636E\u534F\u8BAE\u8FDB\u884C\u64CD\u4F5C\uFF0C\u5171\u540C\u6765\u7EF4\u62A4\u7F13\u5B58\u7684\u4E00\u81F4\u6027\u3002\u8FD9\u7C7B\u534F\u8BAE\u6709 MSI\u3001MESI\u3001MOSI\u3001\u548C Dragon Protocol \u7B49\u3002</p><blockquote><p>\u4E3A\u4E86\u63D0\u5347\u6027\u80FD\u5728 CPU \u548C\u4E3B\u5185\u5B58\u4E4B\u95F4\u589E\u52A0\u4E86\u9AD8\u901F\u7F13\u5B58\uFF0C\u4F46\u5728\u591A\u7EBF\u7A0B\u5E76\u53D1\u573A\u666F\u53EF\u80FD\u4F1A\u9047\u5230<code>\u7F13\u5B58\u4E00\u81F4\u6027</code>\u95EE\u9898\u3002\u90A3\u8FD8\u6709\u6CA1\u6709\u529E\u6CD5\u8FDB\u4E00\u6B65\u63D0\u5347 CPU \u7684\u6267\u884C\u6548\u7387\u5462? \u7B54\u6848\u662F\uFF1A<code>\u5904\u7406\u5668\u4F18\u5316</code>\u3002</p></blockquote><h3 id="\u5904\u7406\u5668\u4F18\u5316\u548C\u6307\u4EE4\u91CD\u6392\u5E8F" tabindex="-1"><a class="header-anchor" href="#\u5904\u7406\u5668\u4F18\u5316\u548C\u6307\u4EE4\u91CD\u6392\u5E8F" aria-hidden="true">#</a> \u5904\u7406\u5668\u4F18\u5316\u548C\u6307\u4EE4\u91CD\u6392\u5E8F</h3><blockquote><p>\u4EC0\u4E48\u662F\u5904\u7406\u5668\u4F18\u5316\uFF1F</p></blockquote><p>\u4E3A\u4E86\u4F7F\u5904\u7406\u5668\u5185\u90E8\u7684\u8FD0\u7B97\u5355\u5143\u80FD\u591F\u6700\u5927\u5316\u88AB\u5145\u5206\u5229\u7528\uFF0C<strong>\u5904\u7406\u5668\u4F1A\u5BF9\u8F93\u5165\u4EE3\u7801\u8FDB\u884C\u4E71\u5E8F\u6267\u884C\u5904\u7406\uFF0C\u8FD9\u5C31\u662F\u5904\u7406\u5668\u4F18\u5316\u3002</strong></p><blockquote><p>\u9664\u4E86\u5904\u7406\u5668\u4F1A\u5BF9\u4EE3\u7801\u8FDB\u884C\u4F18\u5316\u5904\u7406\uFF0C\u5F88\u591A\u73B0\u4EE3\u7F16\u7A0B\u8BED\u8A00\u7684\u7F16\u8BD1\u5668\u4E5F\u4F1A\u505A\u7C7B\u4F3C\u7684\u4F18\u5316\uFF0C\u6BD4\u5982\u50CF Java \u7684\u5373\u65F6\u7F16\u8BD1\u5668(<code>JIT</code>)\u4F1A\u505A<code>\u6307\u4EE4\u91CD\u6392\u5E8F</code>\u3002</p></blockquote><p><img src="https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/0acfb6aeda24439daa1ed368cda7d4c2~tplv-k3u1fbpfcp-zoom-1.image" alt="image-20220928141640358"></p><blockquote><p>\u91CD\u6392\u5E8F\u7684\u7C7B\u578B</p></blockquote><ul><li><p><strong>\u7F16\u8BD1\u5668\u4F18\u5316\u7684\u91CD\u6392\u5E8F\uFF1A</strong></p><p>\u7F16\u8BD1\u5668\u5728\u4E0D\u6539\u53D8\u5355\u7EBF\u7A0B\u7A0B\u5E8F\u8BED\u4E49\u653E\u5165\u524D\u63D0\u4E0B\uFF0C\u53EF\u4EE5\u91CD\u65B0\u5B89\u6392\u8BED\u53E5\u7684\u6267\u884C\u987A\u5E8F\u3002</p></li><li><p><strong>\u6307\u4EE4\u7EA7\u5E76\u884C\u7684\u91CD\u6392\u5E8F\uFF1A</strong></p><p>\u73B0\u4EE3\u5904\u7406\u5668\u91C7\u7528\u4E86\u6307\u4EE4\u7EA7\u5E76\u884C\u6280\u672F\u6765\u5C06\u591A\u6761\u6307\u4EE4\u91CD\u53E0\u6267\u884C\u3002\u5982\u679C\u4E0D\u5B58\u5728\u6570\u636E\u4F9D\u8D56\u6027\uFF0C\u5904\u7406\u5668\u53EF\u4EE5\u6539\u53D8\u8BED\u53E5\u5BF9\u5E94\u673A\u5668\u6307\u4EE4\u7684\u6267\u884C\u987A\u5E8F\u3002</p></li><li><p><strong>\u5185\u5B58\u7CFB\u7EDF\u7684\u91CD\u6392\u5E8F\uFF1A</strong></p><p>\u7531\u4E8E\u5904\u7406\u5668\u4F7F\u7528\u7F13\u5B58\u548C\u8BFB\u5199\u7F13\u51B2\u533A\uFF0C\u8FD9\u4F7F\u5F97\u52A0\u8F7D\u548C\u5B58\u50A8\u64CD\u4F5C\u770B\u4E0A\u53BB\u53EF\u80FD\u662F\u5728\u4E71\u5E8F\u6267\u884C\u3002</p></li></ul><blockquote><p>\u5728\u89E3\u51B3\u5B8C\u4EE5\u4E0A\u7684\u95EE\u9898\u4EE5\u540E\uFF0C\u6211\u4EEC\u5F00\u59CB\u771F\u6B63\u7684\u8981\u89E3\u51B3\u300C\u4E3A\u4EC0\u4E48\u8981\u6709\u5185\u5B58\u6A21\u578B\uFF1F\u300D\u7684\u95EE\u9898\u4E86\u3002</p></blockquote><p>\u6211\u4EEC\u4E0A\u9762\u8BB2\u7684\u300C\u7F13\u5B58\u4E00\u81F4\u6027\u300D\u3001\u300C\u5904\u7406\u5668\u4F18\u5316\u300D\u3001\u300C\u6307\u4EE4\u91CD\u6392\u5E8F\u300D\uFF0C\u5206\u522B\u5C31\u662F\u9020\u6210\u6211\u4EECJava\u5E76\u53D1\u7F16\u7A0B\u7684\u4E09\u4E2A\u57FA\u672C\u95EE\u9898\uFF1A\u300C\u53EF\u89C1\u6027\u95EE\u9898\u300D\u3001\u300C\u539F\u5B50\u6027\u95EE\u9898\u300D\u548C\u300C\u6709\u5E8F\u6027\u95EE\u9898\u300D\u7684\u4E3B\u8981\u539F\u56E0\u3002</p><p>\u4E3A\u4E86\u89E3\u51B3\u7531\u4E8E\u591A\u7EBF\u7A0B\u901A\u8FC7\u5171\u4EAB\u5185\u5B58\u8FDB\u884C\u901A\u4FE1\u65F6\uFF0C\u5B58\u5728\u7684\u672C\u5730\u5185\u5B58\u6570\u636E\u4E0D\u4E00\u81F4\u3001\u7F16\u8BD1\u5668\u4F1A\u5BF9\u4EE3\u7801\u6307\u4EE4\u91CD\u6392\u5E8F\u3001\u5904\u7406\u5668\u4F1A\u5BF9\u4EE3\u7801\u4E71\u5E8F\u6267\u884C\u7B49\u5E26\u6765\u7684\u95EE\u9898\u3002\u6211\u4EEC\u5728\u7269\u7406\u673A\u5668\u4E0A\u5B9A\u4E49\u51FA\u4E00\u5957\u5185\u5B58\u6A21\u578B\uFF0C\u89C4\u8303\u5185\u5B58\u7684\u8BFB\u5199\u64CD\u4F5C\u3002\u5185\u5B58\u6A21\u578B\u89E3\u51B3\u5E76\u53D1\u95EE\u9898\u4E3B\u8981\u91C7\u7528\u4E24\u79CD\u65B9\u5F0F\uFF1A\u9650\u5236\u5904\u7406\u5668\u4F18\u5316\u548C\u4F7F\u7528\u5185\u5B58\u5C4F\u969C\u3002</p><h1 id="java-\u5185\u5B58\u6A21\u578B-jmm" tabindex="-1"><a class="header-anchor" href="#java-\u5185\u5B58\u6A21\u578B-jmm" aria-hidden="true">#</a> Java \u5185\u5B58\u6A21\u578B\uFF08JMM\uFF09</h1><blockquote><p>\u9996\u5148\u6700\u91CD\u8981\u7684\u4E00\u70B9\u2014\u2014\u300CJava \u5185\u5B58\u6A21\u578B\uFF08<code>JMM</code>\uFF09\u300D\u6307\u7684\u4E0D\u662F \u300CJava\u8FD0\u884C\u65F6\u6570\u636E\u533A\u57DF\u300D\uFF01</p></blockquote><p>\u300CJava \u8FD0\u884C\u65F6\u6570\u636E\u533A\u57DF\u300D\u662F\u6211\u4EEC\u770B\u516B\u80A1\u6587\u5E38\u770B\u5230\u7684\uFF1A&quot; Java\u5185\u5B58\u533A\u57DF\u4E94\u5927\u5757\uFF1A\u5806\u3001\u65B9\u6CD5\u533A\u3001\u865A\u62DF\u673A\u6808\u3001\u672C\u5730\u65B9\u6CD5\u6808\u3001PC\u5BC4\u5B58\u5668... &quot;</p><p>\u300CJava \u5185\u5B58\u6A21\u578B\uFF08<code>JMM</code>\uFF09\u300D\u662F\u4EC0\u4E48\u5728\u672C\u7AE0\u4F1A\u505A\u89E3\u7B54\u3002\u63A5\u7740\u5F80\u4E0B\u770B\u5427\uFF01</p><blockquote><p>\u4EC0\u4E48\u662F <code>JMM</code>\uFF1F</p></blockquote><p><code>JMM</code>\uFF08Java Memory Model\uFF09\u662FJava\u5185\u5B58\u6A21\u578B\uFF0C\u7528\u4E8E<strong>\u5C4F\u853D\u6389\u5404\u79CD\u786C\u4EF6\u548C\u64CD\u4F5C\u7CFB\u7EDF\u7684\u5185\u5B58\u8BBF\u95EE\u5DEE\u5F02</strong>\uFF0C\u4EE5\u5B9E\u73B0\u8BA9Java\u7A0B\u5E8F\u5728<strong>\u5404\u79CD\u5E73\u53F0\u4E0B\u90FD\u80FD\u8FBE\u5230\u4E00\u81F4\u7684\u5E76\u53D1\u6548\u679C\u3002</strong></p><p><code>JMM</code> <strong>\u672C\u8EAB\u662F\u4E00\u79CD\u62BD\u8C61\u7684\u6982\u5FF5\uFF0C\u5B9E\u9645\u4E0A\u4E0D\u5B58\u5728\uFF0C\u5B83\u63CF\u8FF0\u7684\u662F\u4E00\u7EC4\u89C4\u8303</strong>\u3002\u901A\u8FC7\u8FD9\u7EC4\u89C4\u8303\u5B9A\u4E49\u4E86\u7A0B\u5E8F\u4E2D\u5404\u4E2A\u53D8\u91CF\uFF08\u5305\u62EC\u5B9E\u4F8B\u5B57\u6BB5\uFF0C\u9759\u6001\u5B57\u6BB5\u548C\u6784\u6210\u6570\u7EC4\u5BF9\u8C61\u7684\u5143\u7D20\uFF09\u7684\u8BBF\u95EE\u65B9\u5F0F\u3002</p><p><code>JMM</code> <strong>\u89C4\u8303\u4E86Java\u865A\u62DF\u673A\u4E0E\u8BA1\u7B97\u673A\u5185\u5B58\u662F\u5982\u4F55\u534F\u540C\u5DE5\u4F5C\u7684</strong>\uFF1A\u89C4\u5B9A\u4E86\u4E00\u4E2A\u7EBF\u7A0B\u5982\u4F55\u548C\u4F55\u65F6\u53EF\u4EE5\u770B\u5230\u7531\u5176\u4ED6\u7EBF\u7A0B\u4FEE\u6539\u8FC7\u540E\u7684\u5171\u4EAB\u53D8\u91CF\u7684\u503C\uFF0C\u4EE5\u53CA\u5728\u5FC5\u987B\u65F6\u5982\u4F55\u540C\u6B65\u7684\u8BBF\u95EE\u5171\u4EAB\u53D8\u91CF\u3002</p><blockquote><p><code>JMM</code> \u7684\u51E0\u4E2A\u89C4\u8303</p></blockquote><ul><li>\u6240\u6709\u53D8\u91CF\u5B58\u50A8\u5728\u4E3B\u5185\u5B58</li></ul><ul><li>\u4E3B\u5185\u5B58\u662F\u865A\u62DF\u673A\u5185\u5B58\u7684\u4E00\u90E8\u5206</li></ul><ul><li>\u6BCF\u6761\u7EBF\u7A0B\u6709\u81EA\u5DF1\u7684\u5DE5\u4F5C\u5185\u5B58</li></ul><ul><li>\u7EBF\u7A0B\u7684\u5DE5\u4F5C\u5185\u5B58\u4FDD\u5B58\u53D8\u91CF\u7684\u4E3B\u5185\u5B58\u526F\u672C</li></ul><ul><li>\u7EBF\u7A0B\u5BF9\u53D8\u91CF\u7684\u64CD\u4F5C\u5FC5\u987B\u5728\u5DE5\u4F5C\u5185\u5B58\u4E2D\u8FDB\u884C</li></ul><ul><li>\u4E0D\u540C\u7EBF\u7A0B\u4E4B\u95F4\u65E0\u6CD5\u76F4\u63A5\u8BBF\u95EE\u5BF9\u65B9\u5DE5\u4F5C\u5185\u5B58\u4E2D\u7684\u53D8\u91CF</li></ul><ul><li>\u7EBF\u7A0B\u95F4\u53D8\u91CF\u503C\u7684\u4F20\u9012\u5747\u9700\u8981\u901A\u8FC7\u4E3B\u5185\u5B58\u6765\u5B8C\u6210</li></ul><h2 id="java\u8FD0\u884C\u65F6\u5185\u5B58\u533A\u57DF\u4E0E\u786C\u4EF6\u5185\u5B58\u7684\u5173\u7CFB" tabindex="-1"><a class="header-anchor" href="#java\u8FD0\u884C\u65F6\u5185\u5B58\u533A\u57DF\u4E0E\u786C\u4EF6\u5185\u5B58\u7684\u5173\u7CFB" aria-hidden="true">#</a> Java\u8FD0\u884C\u65F6\u5185\u5B58\u533A\u57DF\u4E0E\u786C\u4EF6\u5185\u5B58\u7684\u5173\u7CFB</h2><p>Java \u8FD0\u884C\u65F6\u5185\u5B58\u533A\u57DF\u662F\u5206\u6210\u6808\u548C\u5806\u7684\uFF0C\u4F46\u8FD9\u4E9B\u90FD\u662F<code>JVM</code>\u5B9A\u4E49\u7684\u903B\u8F91\u6982\u5FF5\u3002<strong>\u5728\u4F20\u7EDF\u7684\u786C\u4EF6\u5185\u5B58\u67B6\u6784\u4E2D\u6CA1\u6709\u6808\u548C\u5806\u8FD9\u79CD\u6982\u5FF5\u3002</strong></p><blockquote><p>Java \u8FD0\u884C\u65F6\u5185\u5B58\u533A\u57DF\u4E0E\u786C\u4EF6\u4E4B\u95F4\u7684\u5173\u7CFB\u56FE</p></blockquote><p><img src="https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/900da9eec82a46fcaa58efaf3c392e14~tplv-k3u1fbpfcp-zoom-1.image" alt="image-20221002191805847"></p><p>\u7531\u4E0A\u56FE\u53EF\u77E5\uFF0C\u6808\u548C\u5806\u65E2\u5B58\u5728\u4E8E\u9AD8\u901F\u7F13\u5B58\u4E2D\u53C8\u5B58\u5728\u4E8E\u4E3B\u5B58\u4E2D\u3002</p><p>\u8FD9\u4E24\u8005\u5728\u786C\u4EF6\u4E0A\u7684\u5206\u5E03\u5E76\u4E0D\u662F\u9694\u79BB\u7684\uFF0C\u90FD\u4E0D\u8FC7\u662F<code>JVM</code>\u5728\u5B58\u50A8\u8BBE\u5907\u4E0A\u7684\u62BD\u8C61\u5212\u5206\u3002</p><h2 id="java\u7EBF\u7A0B\u4E0E\u4E3B\u5B58\u7684\u5173\u7CFB" tabindex="-1"><a class="header-anchor" href="#java\u7EBF\u7A0B\u4E0E\u4E3B\u5B58\u7684\u5173\u7CFB" aria-hidden="true">#</a> Java\u7EBF\u7A0B\u4E0E\u4E3B\u5B58\u7684\u5173\u7CFB</h2><p><code>JVM</code> \u8FD0\u884C\u7A0B\u5E8F\u7684\u5B9E\u4F53\u662F\u7EBF\u7A0B\uFF0C\u800C\u6BCF\u4E2A\u7EBF\u7A0B\u521B\u5EFA\u65F6 <code>JVM</code> \u90FD\u4F1A\u4E3A\u5176\u521B\u5EFA\u4E00\u4E2A\u5DE5\u4F5C\u5185\u5B58\uFF08\u6709\u4E9B\u5730\u65B9\u79F0\u4E3A\u6808\u7A7A\u95F4\uFF09\uFF0C\u5DE5\u4F5C\u5185\u5B58\u662F\u6BCF\u4E2A\u7EBF\u7A0B\u7684\u79C1\u6709\u6570\u636E\u533A\u57DF\u3002</p><p>\u800C <code>JMM</code> \u89C4\u5B9A\u4E86\u6240\u6709\u53D8\u91CF\u90FD\u5FC5\u987B\u5B58\u50A8\u5728\u4E3B\u5B58\u4E2D\u3002\u4E3B\u5185\u5B58\u662F\u5171\u4EAB\u5185\u5B58\u533A\u57DF\uFF0C\u6240\u6709\u7EBF\u7A0B\u90FD\u53EF\u4EE5\u8BBF\u95EE\u3002<strong>\u4F46\u662F\u7EBF\u7A0B\u5BF9\u53D8\u91CF\u7684\u64CD\u4F5C\u90FD\u5FC5\u987B\u5728\u5DE5\u4F5C\u5185\u5B58\u4E2D\u8FDB\u884C</strong>\uFF0C\u9996\u5148\u9700\u8981\u5C06\u53D8\u91CF\u4ECE\u4E3B\u5B58\u62F7\u8D1D\u5230\u81EA\u5DF1\u7684\u5DE5\u4F5C\u5185\u5B58\u7A7A\u95F4\uFF0C\u7136\u540E\u5BF9\u53D8\u91CF\u8FDB\u884C\u64CD\u4F5C\uFF0C\u64CD\u4F5C\u5B8C\u6210\u540E\u518D\u5C06\u53D8\u91CF\u5199\u56DE\u4E3B\u5185\u5B58\u3002</p><p>\u7EBF\u7A0B\u4E0D\u80FD\u76F4\u63A5\u64CD\u4F5C\u4E3B\u5B58\u4E2D\u7684\u53D8\u91CF\uFF0C\u5404\u4E2A\u7EBF\u7A0B\u4E2D\u7684\u5DE5\u4F5C\u5185\u5B58\u4E2D\u5B58\u50A8\u7740\u4E3B\u5B58\u4E2D\u7684\u53D8\u91CF\u62F7\u8D1D\u526F\u672C\uFF0C\u56E0\u6B64\u4E0D\u540C\u7684\u7EBF\u7A0B\u95F4\u65E0\u6CD5\u8BBF\u95EE\u5BF9\u65B9\u7684\u5DE5\u4F5C\u5185\u5B58\uFF0C\u7EBF\u7A0B\u95F4\u7684\u901A\u4FE1\uFF08\u4F20\u503C\uFF09\u5FC5\u987B\u901A\u8FC7\u4E3B\u5B58\u6765\u5B8C\u6210\u3002</p><blockquote><p><code>JMM</code> \u89C4\u8303\u5B9A\u4E49\u7684\u51E0\u4E2A\u89C4\u8303\uFF0C\u5E76\u4E14\u6839\u636E\u51E0\u4E2A\u89C4\u8303\u4F5C\u56FE</p></blockquote><ul><li>\u6240\u6709\u7684\u53D8\u91CF\u90FD\u5B58\u50A8\u5728\u4E3B\u5185\u5B58\u4E2D\u3002</li><li>\u6BCF\u4E2A\u7EBF\u7A0B\u90FD\u6709\u4E00\u4E2A\u79C1\u6709\u7684\u672C\u5730\u5185\u5B58(Local Memory)\uFF0C\u672C\u5730\u5185\u5B58\u4E2D\u5B58\u50A8\u4E86\u8BE5\u7EBF\u7A0B\u4EE5\u8BFB/\u5199\u5171\u4EAB\u53D8\u91CF\u7684\u62F7\u8D1D\u526F\u672C\u3002</li><li>\u7EBF\u7A0B\u5BF9\u53D8\u91CF\u7684\u6240\u6709\u64CD\u4F5C\u90FD\u5FC5\u987B\u5728\u672C\u5730\u5185\u5B58\u4E2D\u8FDB\u884C\uFF0C\u800C\u4E0D\u80FD\u76F4\u63A5\u8BFB\u5199\u4E3B\u5185\u5B58\u3002</li><li>\u4E0D\u540C\u7684\u7EBF\u7A0B\u4E4B\u95F4\u65E0\u6CD5\u76F4\u63A5\u8BBF\u95EE\u5BF9\u65B9\u672C\u5730\u5185\u5B58\u4E2D\u7684\u53D8\u91CF\u3002</li></ul><p><img src="https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/bbf2a65d92aa426cb31de830bfca1cec~tplv-k3u1fbpfcp-zoom-1.image" alt="image-20221002190823796"></p><p>\u7EBF\u7A0B\u4E4B\u95F4\u62E5\u6709\u7684\u672C\u5730\u5185\u5B58\u662F\u903B\u8F91\u4E0A\u9694\u79BB\uFF0C\u800C\u7269\u7406\u4E0A\u4E0D\u9694\u79BB\u7684\u3002</p><h2 id="jmm\u4E0B\u7684\u7EBF\u7A0B\u95F4\u901A\u4FE1" tabindex="-1"><a class="header-anchor" href="#jmm\u4E0B\u7684\u7EBF\u7A0B\u95F4\u901A\u4FE1" aria-hidden="true">#</a> JMM\u4E0B\u7684\u7EBF\u7A0B\u95F4\u901A\u4FE1</h2><p>\u63A5\u4E0B\u6765\u6211\u4EEC\u4F5C\u56FE\u6F14\u793A\u5982\u4E0B\u64CD\u4F5C\uFF1A</p><p>\u4E24\u4E2A\u7EBF\u7A0B\u90FD\u5BF9\u4E00\u4E2A\u5171\u4EAB\u53D8\u91CF\u8FDB\u884C\u64CD\u4F5C\uFF0C\u5171\u4EAB\u53D8\u91CF\u521D\u59CB\u503C\u4E3A 1\uFF0C\u7EBF\u7A0B1\u5BF9\u5B83\u8FDB\u884C\u52A0 1\uFF0C\u63A5\u7740\u7EBF\u7A0B2\u5BF9\u5B83\u8FDB\u884C\u52A01\uFF0C\u9884\u671F\u5171\u4EAB\u53D8\u91CF\u7684\u503C\u4E3A 3\u3002</p><blockquote><p><code>JMM</code> \u89C4\u8303\u4E0B\u7684\u64CD\u4F5C\uFF1A</p></blockquote><p><img src="https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/dceaf9bfffec49d29ca2afe63c83959a~tplv-k3u1fbpfcp-zoom-1.image" alt="image-20221002192522693"></p><ul><li><p><strong>\u6B65\u9AA4\u4E00\uFF1A</strong></p><p>\u7EBF\u7A0B1\u4ECE\u4E3B\u5B58\u4E2D\u8BFB\u53D6A\uFF0C\u8FDB\u884C\u52A0\u4E00\u66F4\u65B0\uFF0C\u5C06\u672C\u5730\u5185\u5B581\u4E2D\u66F4\u65B0\u8FC7\u7684\u5171\u4EAB\u53D8\u91CFA\uFF08\u6B64\u65F6\u4E3A2\uFF09\u5237\u65B0\u5230\u4E3B\u5B58\u4E2D\u53BB</p></li><li><p><strong>\u6B65\u9AA4\u4E8C\uFF1A</strong></p><p>\u7EBF\u7A0B2\u4ECE\u4E3B\u5B58\u4E2D\u8BFB\u53D6\u7EBF\u7A0B1\u66F4\u65B0\u8FC7\u7684\u5171\u4EAB\u53D8\u91CFA\uFF08\u6B64\u65F6\u4E3A2\uFF09\uFF0C\u5E76\u8FDB\u884C\u52A0\u4E00\u66F4\u65B0\uFF0C\u6B64\u65F6\u4E3A3\uFF0C\u518D\u5237\u65B0\u5230\u4E3B\u5B58\u4E2D\u53BB\u3002</p></li></ul><h2 id="jmm\u6570\u636E\u539F\u5B50\u64CD\u4F5C" tabindex="-1"><a class="header-anchor" href="#jmm\u6570\u636E\u539F\u5B50\u64CD\u4F5C" aria-hidden="true">#</a> JMM\u6570\u636E\u539F\u5B50\u64CD\u4F5C</h2><blockquote><p>\u4E3A\u4E86\u66F4\u597D\u7684\u63A7\u5236\u4E3B\u5185\u5B58\u548C\u672C\u5730\u5185\u5B58\u7684\u4EA4\u4E92\uFF0C<code>JMM</code> \u5B9A\u4E49\u4E86\u516B\u79CD\u64CD\u4F5C\u6765\u5B9E\u73B0\uFF1A</p></blockquote><ul><li><p><code>lock</code>\uFF1A</p><p>\u9501\u5B9A\u3002\u4F5C\u7528\u4E8E\u4E3B\u5185\u5B58\u7684\u53D8\u91CF\uFF0C\u628A\u4E00\u4E2A\u53D8\u91CF\u6807\u8BC6\u4E3A\u4E00\u6761\u7EBF\u7A0B\u72EC\u5360\u72B6\u6001\u3002</p><p>\u52A0\u9501\u65F6\u7684\u64CD\u4F5C\u3002</p></li><li><p><code>unlock</code>\uFF1A</p><p>\u89E3\u9501\u3002\u4F5C\u7528\u4E8E\u4E3B\u5185\u5B58\u53D8\u91CF\uFF0C\u628A\u4E00\u4E2A\u5904\u4E8E\u9501\u5B9A\u72B6\u6001\u7684\u53D8\u91CF\u91CA\u653E\u51FA\u6765\uFF0C\u91CA\u653E\u540E\u7684\u53D8\u91CF\u624D\u53EF\u4EE5\u88AB\u5176\u4ED6\u7EBF\u7A0B\u9501\u5B9A\u3002</p></li><li><p><code>read</code>\uFF1A</p><p>\u8BFB\u53D6\u3002\u4F5C\u7528\u4E8E\u4E3B\u5185\u5B58\u53D8\u91CF\uFF0C\u628A\u4E00\u4E2A\u53D8\u91CF\u503C\u4ECE\u4E3B\u5185\u5B58\u4F20\u8F93\u5230\u7EBF\u7A0B\u7684\u5DE5\u4F5C\u5185\u5B58\u4E2D\uFF0C\u4EE5\u4FBF\u968F\u540E\u7684<code>load</code>\u52A8\u4F5C\u4F7F\u7528</p></li><li><p><code>load</code>\uFF1A</p><p>\u8F7D\u5165\u3002\u4F5C\u7528\u4E8E\u5DE5\u4F5C\u5185\u5B58\u7684\u53D8\u91CF\uFF0C\u5B83\u628A<code>read</code>\u64CD\u4F5C\u4ECE\u4E3B\u5185\u5B58\u4E2D\u5F97\u5230\u7684\u53D8\u91CF\u503C\u653E\u5165\u5DE5\u4F5C\u5185\u5B58\u7684\u53D8\u91CF\u526F\u672C\u4E2D\u3002</p></li><li><p><code>use</code>\uFF1A</p><p>\u4F7F\u7528\u3002\u4F5C\u7528\u4E8E\u5DE5\u4F5C\u5185\u5B58\u7684\u53D8\u91CF\uFF0C\u628A\u5DE5\u4F5C\u5185\u5B58\u4E2D\u7684\u4E00\u4E2A\u53D8\u91CF\u503C\u4F20\u9012\u7ED9\u6267\u884C\u5F15\u64CE\uFF0C\u6BCF\u5F53\u865A\u62DF\u673A\u9047\u5230\u4E00\u4E2A\u9700\u8981\u4F7F\u7528\u53D8\u91CF\u7684\u503C\u7684\u5B57\u8282\u7801\u6307\u4EE4\u65F6\u5C06\u4F1A\u6267\u884C\u8FD9\u4E2A\u64CD\u4F5C\u3002</p></li><li><p><code>assign</code>\uFF1A</p><p>\u8D4B\u503C\u3002\u4F5C\u7528\u4E8E\u5DE5\u4F5C\u5185\u5B58\u7684\u53D8\u91CF\uFF0C\u5B83\u628A\u4E00\u4E2A\u4ECE\u6267\u884C\u5F15\u64CE\u63A5\u6536\u5230\u7684\u503C\u8D4B\u503C\u7ED9\u5DE5\u4F5C\u5185\u5B58\u7684\u53D8\u91CF\uFF0C\u6BCF\u5F53\u865A\u62DF\u673A\u9047\u5230\u4E00\u4E2A\u7ED9\u53D8\u91CF\u8D4B\u503C\u7684\u5B57\u8282\u7801\u6307\u4EE4\u65F6\u6267\u884C\u8FD9\u4E2A\u64CD\u4F5C\u3002</p></li><li><p><code>store</code>\uFF1A</p><p>\u5B58\u50A8\u3002\u4F5C\u7528\u4E8E\u5DE5\u4F5C\u5185\u5B58\u7684\u53D8\u91CF\uFF0C\u628A\u5DE5\u4F5C\u5185\u5B58\u4E2D\u7684\u4E00\u4E2A\u53D8\u91CF\u7684\u503C\u4F20\u9001\u5230\u4E3B\u5185\u5B58\u4E2D\uFF0C\u4EE5\u4FBF\u968F\u540E\u7684<code>write</code>\u7684\u64CD\u4F5C\u3002</p></li><li><p><code>write</code>\uFF1A</p><p>\u5199\u5165\u3002\u4F5C\u7528\u4E8E\u4E3B\u5185\u5B58\u7684\u53D8\u91CF\uFF0C\u5B83\u628A<code>store</code>\u64CD\u4F5C\u4ECE\u5DE5\u4F5C\u5185\u5B58\u4E2D\u4E00\u4E2A\u53D8\u91CF\u7684\u503C\u4F20\u9001\u5230\u4E3B\u5185\u5B58\u7684\u53D8\u91CF\u4E2D\u3002</p></li></ul><blockquote><p>\u6CE8\u610F\uFF1A\u5DE5\u4F5C\u5185\u5B58\u4E5F\u5C31\u662F\u672C\u5730\u5185\u5B58\u7684\u610F\u601D\u3002</p></blockquote><blockquote><p>\u516B\u79CD\u64CD\u4F5C\u7684\u8981\u70B9</p></blockquote><ul><li><code>JVM</code> \u5B9E\u73B0\u65F6\u5FC5\u987B\u4FDD\u8BC1\u4E0A\u9762\u7684\u6BCF\u79CD\u64CD\u4F5C\u90FD\u662F<strong>\u539F\u5B50\u7684</strong>\u3002</li></ul><ul><li><code>JMM</code> \u53EA\u8981\u6C42\u4E0A\u8FF0\u64CD\u4F5C\u5FC5\u987B\u6309\u987A\u5E8F\u6267\u884C\uFF0C\u800C\u6CA1\u6709\u4FDD\u8BC1\u5FC5\u987B\u662F\u8FDE\u7EED\u6267\u884C\u3002</li></ul><ul><li>\u5982\u679C\u8981\u628A\u4E00\u4E2A\u53D8\u91CF\u4ECE\u4E3B\u5B58\u4E2D\u590D\u5236\u5230\u5DE5\u4F5C\u5185\u5B58\u4E2D\uFF0C\u5219\u9700\u8981\u6309\u5E8F\u6267\u884C<code>read</code>\u548C<code>load</code>\u64CD\u4F5C\u3002</li><li>\u5982\u679C\u8981\u628A\u4E00\u4E2A\u53D8\u91CF\u4ECE\u4E3B\u5B58\u4E2D\u540C\u6B65\u56DE\u4E3B\u5B58\u4E2D\uFF0C\u5219\u9700\u8981\u6309\u5E8F\u6267\u884C<code>store</code>\u548C<code>write</code>\u64CD\u4F5C\u3002</li></ul><blockquote><p>\u4EA4\u4E92\u64CD\u4F5C\u7684\u6D41\u7A0B</p></blockquote><p>\u6309\u7167\uFF1A\u300C<strong>\u4E3B\u5185\u5B58\u2192read\u2192load\u2192\u5DE5\u4F5C\u5185\u5B58\u2192use\u2192\u4E1A\u52A1\u65B9\u6CD5\u2192assign\u2192\u5DE5\u4F5C\u5185\u5B58\u2192store\u2192write\u2192\u4E3B\u5185\u5B58</strong>\u300D\u7684\u987A\u5E8F\u8FDB\u884C\u4EA4\u4E92</p><p><img src="https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/3f116436ff0246958c0bd1e0150b3f33~tplv-k3u1fbpfcp-zoom-1.image" alt="image-20221002213731899"></p><blockquote><p>\u89E3\u91CA</p></blockquote><ul><li><blockquote><p>\u4EC0\u4E48\u662F\u603B\u7EBF\uFF1F</p></blockquote><p>\u8FD9\u91CC\u7684\u603B\u7EBF\u5B9E\u9645\u4E0A\u5C31\u662F\u786C\u4EF6\u4E4B\u95F4\u7684\u5173\u8054\u3002\u786C\u4EF6\u4E4B\u95F4\u7684\u6570\u636E\u4F20\u9012\u9760\u7684\u5C31\u662F\u603B\u7EBF\uFF0C\u9760\u7740\u603B\u7EBF\u7684\u6982\u5FF5\u6211\u4EEC\u5728\u7814\u7A76\u95EE\u9898\u7684\u65F6\u5019\u53EF\u4EE5\u5FFD\u7565\u80CC\u540E\u590D\u6742\u7684\u786C\u4EF6\u7ED3\u6784\u3002</p></li><li><blockquote><p><code>read</code>\uFF0C<code>load</code>\uFF0C<code>use</code>\uFF0C<code>assign</code></p></blockquote><p>\u5149\u753B\u56FE\u548C\u6587\u5B57\u53EF\u80FD\u8FD8\u662F\u6BD4\u8F83\u62BD\u8C61\uFF0C\u8FD9\u91CC\u6211\u76F4\u63A5\u5199\u597D\u4E86\u4EE3\u7801\uFF0C\u53EF\u4EE5\u548C\u56FE\u7ED3\u5408\u7406\u89E3\u3002</p><p>\u8FD9\u91CC\u6211\u4EEC\u5B9A\u4E49\u4E86\u4E00\u4E2A\u7EBF\u7A0B1\uFF1A</p><p><img src="https://p6-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/52cfae994f704bd38d598c28360182e6~tplv-k3u1fbpfcp-watermark.image?" alt="image.png"></p><p>\u7EBF\u7A0B1\u8FD0\u884C\u5E76\u9047\u5230<code>initFlag</code>\uFF0C\u5219\u4F9D\u6B21\u6267\u884C<code>read</code>\u548C<code>load</code>\u64CD\u4F5C\uFF0C\u5C06\u53D8\u91CF\u4ECE\u4E3B\u5B58\u4E2D\u52A0\u8F7D\u5230\u5DE5\u4F5C\u5185\u5B58\u4E2D\u3002</p><p>\u7136\u540E\u6211\u4EEC\u770B\u5230<code>!initFlag</code>\u8FD9\u4E2A\u8BED\u53E5\u5C31\u662F\u8FDB\u884C<code>use</code>\u64CD\u4F5C\u4E86\uFF0C\u5BF9\u5DE5\u4F5C\u5185\u5B58\u8BFB\u53D6\u6570\u636E\u5230\u7EBF\u7A0B\u91CC\u9762\u6765\u8FDB\u884C\u8BA1\u7B97\u3002</p><p>\u7136\u540E\u6839\u636E\u4EE3\u7801\u7684\u5FAA\u73AF\u903B\u8F91CPU\u5C31\u5361\u5728\u8FD9\u91CC\u4E0D\u65AD\u5730\u5BF9<code>initFlag</code>\u505A\u53D6\u53CD\u64CD\u4F5C</p><p>\u540C\u65F6\u6211\u4EEC\u8FD9\u91CC\u8FD8\u6709\u4E00\u4E2A\u7EBF\u7A0B2\uFF0C\u4E00\u6837\u5730\u8981\u5BF9<code>initFlag</code>\u8FDB\u884C<code>read</code>\uFF0C<code>load</code>\uFF0C<code>use</code>\u3002\u5173\u952E\u5728\u4E8E\u5BF9<code>initFlag</code>\u505A\u4E86\u4E00\u6B21\u8D4B\u503C\u64CD\u4F5C<code>assign</code></p><p><img src="https://p6-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/1937282d43f14504aa75b8f694b1c303~tplv-k3u1fbpfcp-watermark.image?" alt="image.png"></p><p>\u8FD9\u4E2A\u65F6\u5019\u5DE5\u4F5C\u5185\u5B58\u4E2D\u7684<code>initFlag</code>\u5DF2\u7ECF\u4ECE<code>false</code>\u53D8\u6210<code>true</code>\u4E86\u3002</p></li><li><blockquote><p><code>store</code>\uFF0C<code>write</code></p></blockquote><p>\u7EBF\u7A0B2\u8D4B\u503C\u5B8C\u6210\u4EE5\u540E\uFF0C\u5171\u4EAB\u53D8\u91CF<code>initFlag</code>\u7684\u526F\u672C\u503C\u4F1A\u540C\u6B65\u56DE\u6211\u4EEC\u7684\u4E3B\u5B58</p><p><img src="https://p9-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/0c6c12320e7b4d5f8e29bca7275828ed~tplv-k3u1fbpfcp-watermark.image?" alt="image.png"></p><p>\u8FD9\u4E2A\u540C\u6B65\u7684\u8FC7\u7A0B\u5E95\u5C42\u505A\u7684\u64CD\u4F5C\u5C31\u662F<code>store</code>\uFF0C\u628A\u5DE5\u4F5C\u5185\u5B58\u4E2D\u7684\u53D8\u91CF\u7684\u503C\u4F20\u9001\u5230\u4E3B\u5B58\u4E2D\u3002</p><p>\u4F46\u662F\u8FD9\u4E2A\u65F6\u5019\u53EA\u662F\u6682\u65F6\u653E\u5165\u5230\u4E3B\u5B58\uFF0C\u5E76\u6CA1\u6709\u8D4B\u503C\u7ED9\u4E3B\u5B58\u4E2D\u7684\u5171\u4EAB\u53D8\u91CF\u3002\u76F4\u5230\u5B83\u8FDB\u884C\u4E86<code>write</code>\u64CD\u4F5C\u3002</p><p>\u5728\u8FD9\u4E4B\u540E\u4E3B\u5B58\u4E2D\u7684<code>initFlag</code>\u624D\u4F1A\u53D8\u6210<code>true</code></p></li></ul><blockquote><p>\u5173\u4E8E\u7F13\u5B58\u4E00\u81F4\u6027\u95EE\u9898\u7684\u8BE6\u7EC6\u89E3\u51B3\u65B9\u6CD5\u4F1A\u5728\u540E\u9762\u591A\u7EBF\u7A0B\u7AE0\u8282\u7684\u535A\u5BA2\u91CC\u89E3\u51B3\u3002</p></blockquote><h1 id="\u5C0F\u7ED3" tabindex="-1"><a class="header-anchor" href="#\u5C0F\u7ED3" aria-hidden="true">#</a> \u5C0F\u7ED3</h1><p>\u672C\u7AE0\u4E3A<code>JVM</code>\u5B66\u4E60\u7684\u5165\u95E8\u7AE0\u8282\uFF0C\u540C\u65F6\u4E0E<code>JUC</code>\u5E76\u53D1\u7F16\u7A0B\u7AE0\u8282\u6709\u5F88\u5F3A\u7684\u5173\u8054\u6027\u3002\u53EF\u4EE5\u6839\u636E\u540E\u9762\u7684\u5B66\u4E60\u53CD\u590D\u7684\u8FDB\u884C\u590D\u4E60\u548C\u603B\u7ED3\u3002</p><p>\u672C\u6587\u53C2\u8003\uFF1A</p><ul><li><a href="https://www.sohu.com/a/587381197_121266922" target="_blank" rel="noopener noreferrer">https://www.sohu.com/a/587381197_121266922</a></li><li><a href="https://www.51cto.com/article/658158.html" target="_blank" rel="noopener noreferrer">https://www.51cto.com/article/658158.html</a></li><li><a href="https://doocs.github.io/jvm" target="_blank" rel="noopener noreferrer">https://doocs.github.io/jvm</a></li><li><a href="https://zhuanlan.zhihu.com/p/29881777" target="_blank" rel="noopener noreferrer">https://zhuanlan.zhihu.com/p/29881777</a></li><li><a href="https://juejin.cn/post/6888968995253256206" target="_blank" rel="noopener noreferrer">https://juejin.cn/post/6888968995253256206</a></li><li><a href="https://www.bilibili.com/video/BV1RE411q7Yd?p=3&amp;vd_source=b2410566bdc7ecf118a23188bd71b4de" target="_blank" rel="noopener noreferrer">https://www.bilibili.com/video/BV1RE411q7Yd?p=3&amp;vd_source=b2410566bdc7ecf118a23188bd71b4de</a></li></ul>',79);function a(t,l){return p}var i=e(c,[["render",a],["__file","index.html.vue"]]);export{i as default};
